import { createNewProgram } from '../utils/programStructures';
import { initialState } from './initialState';

function programReducer(state = initialState, action) {
  switch (action.type) {
    case 'SET_INITIAL_PROGRAM':
      return {
        ...state,
        programs: {
          ...state.programs,
          [action.payload.id]: action.payload
        },
        selectedProgramId: action.payload.id
      };

    case 'SET_SELECTED_PROGRAM':
      console.log('SET_SELECTED_PROGRAM action:', action.payload);
      return {
        ...state,
        selectedProgramId: action.payload.id,
        programs: {
          ...state.programs,
          [action.payload.id]: action.payload
        }
      };

    case 'ADD_PROGRAM':
      const newProgram = createNewProgram();
      return {
        ...state,
        programs: {
          ...state.programs,
          [newProgram.tempId]: {
            ...newProgram,
            ...action.payload
          }
        }
      };

    case 'UPDATE_PROGRAM':
      const updatedProgram = action.payload;
      return {
        ...state,
        programs: {
          ...state.programs,
          [updatedProgram.id || updatedProgram.tempId]: updatedProgram
        }
      };

    case 'UPDATE_PROGRAM_SUCCESS':
      const savedProgram = action.payload;
      if (!savedProgram || (!savedProgram.id && !savedProgram.tempId)) {
        console.error('Invalid program data received:', savedProgram);
        return state;
      }
      return {
        ...state,
        programs: {
          ...state.programs,
          [savedProgram.id || savedProgram.tempId]: {
            ...savedProgram,
            isNew: false
          }
        }
      };

    case 'DELETE_PROGRAM':
      const { programId } = action.payload;
      const { [programId]: deletedProgram, ...remainingPrograms } =
        state.programs;
      return {
        ...state,
        programs: remainingPrograms,
        selectedProgramId:
          state.selectedProgramId === programId ? null : state.selectedProgramId
      };

    default:
      return state;
  }
}

export { programReducer };
