  const [selectedCategory, setSelectedEquipment] = useState('Any Equipment');
  const [isOpen, setIsOpen] = useState(false);

  useEffect(() => {
    // Fetch equipment from the API
    fetch('http://localhost:9025/api/equipments')
      .then(response => response.json())
      .then(data => setEqipments(data))
      .catch(error => console.error('Failed to fetch equipments:', error));
