import { actionTypes } from '../actions/actionTypes';

const initialProgramState = {
  program: {
    user_id: null,
    id: null,
    name: '',
    program_duration: 0,
    duration_unit: '',
    days_per_week: 0,
    main_goal: ''
  },
  workout: {
    workouts: [], // Ensure workouts is initialized correctly
    activeWorkout: null
  }
};

function programReducer(state = initialProgramState, action) {
  switch (action.type) {
    case actionTypes.INITIALIZE_EDIT_PROGRAM_STATE:
      return {
        program: action.payload.program,
        workout: {
          workouts: action.payload.workouts,
          activeWorkout: action.payload.activeWorkout
        }
      };

    case actionTypes.INITIALIZE_NEW_PROGRAM_STATE:
      return {
        program: action.payload.program,
        workout: {
          workouts: action.payload.workouts,
          activeWorkout: action.payload.activeWorkout
        }
      };

    case actionTypes.UPDATE_PROGRAM:
      return {
        ...state,
        ...action.payload
      };

    case actionTypes.UPDATE_PROGRAM_SUCCESS:
      return {
        ...state,
        ...action.payload
      };

    case actionTypes.CLEAR_PROGRAM:
      return {
        ...state,
        ...action.payload
      };

    default:
      return state;
  }
}

export { programReducer };
