function exerciseReducer(state, action) {
  switch (action.type) {
    case 'ADD_EXERCISE':
      const { workoutId, exercises } = action.payload;
      if (state.activeWorkout.id === workoutId) {
        return {
          ...state,
          activeWorkout: {
            ...state.activeWorkout,
            exercises: [...state.activeWorkout.exercises, ...exercises]
          }
        };
      }
      return state;

    case 'UPDATE_EXERCISE':
      // action.payload should include the id of the exercise to update
      // and the properties to be updated.
      if (state[action.payload.id]) {
        return {
          ...state,
          [action.payload.id]: {
            ...state[action.payload.id],
            ...action.payload.updates
          }
        };
      }
      return state;

    case 'DELETE_EXERCISE':
      // action.payload should be the id of the exercise to delete.
      const newState = { ...state };
      delete newState[action.payload];
      return newState;

    default:
      return state;
  }
}

export { exerciseReducer };
