import { actionTypes } from '../actions/actionTypes';
import { initialState } from './initialState';

function exerciseReducer(state, action) {
  switch (action.type) {
    case actionTypes.ADD_EXERCISE:
      console.log('initialState:', initialState);
      console.log('Current state:', state);
      const { workoutId, exercise } = action.payload;
      const activeWorkoutId = state.activeWorkout;
      console.log('activeWorkoutId:', activeWorkoutId);
      console.log('action.payload:', action.payload);

      console.log('Current state in exerciseReducer:', state);
      console.log('Current workouts:', state.workouts);
      console.log('Target workoutId:', workoutId);

      // Accessing the workout by ID from the workouts object
      if (!state.workouts[workoutId]) {
        console.error('No workout matches the provided workoutId:', workoutId);
        return state;
      }
      const workout = state.workouts[workoutId];
      console.log('Current workout:', workout);

      // Retrieve current exercises and append the new one
      const currentExercises = state.workouts[workoutId].exercises;
      console.log('Current exercises:', currentExercises);
      const updatedExercises = [...workout.exercises, exercise];
      console.log('Updated exercises:', updatedExercises);

      // Return the updated state with the new exercises list for the specific workout
      return {
        ...state,
        workouts: {
          ...state.workouts,
          [workoutId]: {
            workout,
            exercises: updatedExercises
          }
        }
      };

    case actionTypes.DELETE_EXERCISE:
      console.log('DELETE_EXERCISE action:', action);
      const newState = { ...state };
      delete newState[action.payload];
      return newState;

    default:
      return state;
  }
}

export { exerciseReducer };
