import { actionTypes } from '../actions/actionTypes';
import { initialState } from './initialState';

function exerciseReducer(state = initialState, action) {
  switch (action.type) {
    case actionTypes.ADD_EXERCISE:
      const { exercise } = action.payload;
      const workoutId = state.activeWorkout;

      console.log('state:', state);

      if (!workoutId || !state.workouts[workoutId]) {
        console.error(
          'No active workout or workout does not exist:',
          workoutId
        );
        return state;
      }

      return {
        ...state,
        exercises: {
          ...state.exercises,
          [workoutId]: {
            ...state.exercises[workoutId],
            [exercise.id]: exercise
          }
        }
      };

    case actionTypes.DELETE_EXERCISE:
      const { workoutId: wId, exerciseId } = action.payload;

      if (!wId || !state.exercises[wId]) {
        console.error('No active workout or workout does not exist:', wId);
        return state;
      }

      const { [exerciseId]: deletedExercise, ...remainingExercises } =
        state.exercises[wId];

      return {
        ...state,
        exercises: {
          ...state.exercises,
          [wId]: remainingExercises
        }
      };

    default:
      return state;
  }
}

export { exerciseReducer };
