import React, { useState, useContext, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { v4 as uuidv4 } from 'uuid';
import { ProgramContext } from '../../../contexts/programContext';
import Workout from '../../../components/Workout/Workout';
import ProgramForm from '../../../components/Program/ProgramForm';
import NavBar from '../../../components/Nav/Nav';
import Toggle from '../../../components/Inputs/Toggle';
import Button from '../../../components/Inputs/Button';
import './program.css';

const CreateProgram = () => {
  const { state, saveProgram, dispatch, setActiveWorkout, clearState } =
    useContext(ProgramContext);
  const [expandedWorkouts, setExpandedWorkouts] = useState({});
  // const [programId, setProgramId] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    clearState();
    // Ensure an initial empty workout is only added if it's a new program creation
    const initialProgramId = uuidv4();
    dispatch({ type: 'ADD_PROGRAM', payload: { id: initialProgramId } });
    dispatch({ type: 'ADD_WORKOUT', payload: { programId: initialProgramId } });
  }, [dispatch, clearState]);

  const handleSaveProgram = async () => {
    try {
      await saveProgram(state.programs[programId]);
      navigate('/programs');
    } catch (error) {
      console.error('Failed to save the program:', error);
    }
  };

  const handleExpandWorkout = useCallback(
    workoutId => {
      const isCurrentlyExpanded = expandedWorkouts[workoutId];

      setExpandedWorkouts(prevState => ({
        ...Object.keys(prevState).reduce((acc, key) => {
          acc[key] = false; // collapse all
          return acc;
        }, {}),
        [workoutId]: !isCurrentlyExpanded
      }));

      if (!isCurrentlyExpanded) {
        setActiveWorkout(workoutId, programId);
      } else {
        setActiveWorkout(null, null);
      }
    },
    [expandedWorkouts, setActiveWorkout, programId]
  );

  const handleToggleProgramForm = useCallback(() => {
    setExpandedWorkouts(prevState => ({
      ...Object.keys(prevState).reduce((acc, key) => {
        acc[key] = false; // collapse all workouts
        return acc;
      }, {}),
      program: !prevState.program // toggle the program form
    }));
  }, []);

  const handleCancel = () => {
    clearState();
    navigate('/');
  };

  const handleAddWorkout = event => {
    event.preventDefault();
    dispatch({
      type: 'ADD_WORKOUT',
      payload: { programId }
    });
  };

  if (!state || !state.programs || !state.programs[programId]) {
    return <div>Loading or no programs available...</div>;
  }

  return (
    <div>
      <NavBar isEditing='true' />
      <div className='create-prog-page'>
        <div className='create-prog-page__toggle-container'>
          <Toggle />
        </div>
        <div className='create-prog-page__container'>
          <div className='create-prog-page__left-container'>
            <ProgramForm
              program={state.programs[programId]}
              isEditing={true}
              isExpanded={expandedWorkouts['program']}
              onToggleExpand={handleToggleProgramForm}
            />
            {state.workouts && Object.keys(state.workouts).length > 0 ? (
              Object.values(state.workouts).map(workout => {
                if (!workout || !workout.id) {
                  console.error('Invalid workout object:', workout); // Log invalid workout object
                  return null;
                }
                if (workout.programId === programId) {
                  return (
                    <Workout
                      key={workout.id}
                      workout={workout}
                      isExpanded={expandedWorkouts[workout.id] || false}
                      onToggleExpand={() => handleExpandWorkout(workout.id)}
                    />
                  );
                }
                return null;
              })
            ) : (
              <div>No workouts available</div>
            )}
          </div>
        </div>
        <div className='create-prog-page__button-container'>
          <Button type='button' onClick={handleAddWorkout}>
            Add Workout
          </Button>
          <Button type='submit' onClick={handleSaveProgram}>
            Save
          </Button>
          <Button type='button' onClick={handleCancel}>
            Cancel
          </Button>
        </div>
      </div>
    </div>
  );
};

export default CreateProgram;
