import React, { useState, useEffect, useRef } from 'react';
import './homePage.css';

const HomePage = () => {
  const [isContainerInView, setIsContainerInView] = useState(false);
  const containerRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      if (containerRef.current) {
        const containerRect = containerRef.current.getBoundingClientRect();
        setIsExpanded(
          containerRect.top <= window.innerHeight && containerRect.bottom >= 0
        );
        setIsContainerInView(
          containerRect.top <= window.innerHeight && containerRect.bottom >= 0
        );
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToNextPage = () => {
    window.scrollTo({
      top: window.innerHeight,
      behavior: 'smooth'
    });
  };

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth'
    });
  };

  return (
    <div className='home-page'>
      <div className='full-viewport-div'>
        <h1 className='app-title'>App Title</h1>
        <div className='arrow scroll-down' onClick={scrollToNextPage}>
          &#8595;
        </div>
      </div>
      <div className='container' ref={containerRef}>
        {isContainerInView && (
          <div className='arrow scroll-up' onClick={scrollToTop}>
            &#8593;
          </div>
        )}
        <div className={`expandable-div left ${isExpanded ? 'expanded' : ''}`}>
          Expandable div
        </div>
        <div
          className={`expandable-div center ${isExpanded ? 'expanded' : ''}`}
        >
          Expandable div
        </div>
        <div className={`expandable-div right ${isExpanded ? 'expanded' : ''}`}>
          Expandable div
        </div>
      </div>
    </div>
  );
};

export default HomePage;
