import React from 'react';
import { useTheme } from '../../contexts/themeContext';
import './TextInput.css';

const TextInput = ({
  id,
  name,
  value,
  onChange,
  placeholder,
  type = 'text',
  className,
  onClear,
  ...props
}) => {
  const theme = useTheme();

  return (
    <div className={`text-input-wrapper ${theme}`}>
      <input
        id={id}
        name={name}
        type={type}
        value={value}
        onChange={onChange}
        placeholder={placeholder}
        className={`text-input ${className}`}
        {...props}
      />
      {value && (
        <button
          type='button'
          className='clear-button'
          onClick={onClear}
          aria-label='Clear'
        >
          &times;
        </button>
      )}
    </div>
  );
};

export default TextInput;
