import { actionTypes } from '../actions/actionTypes';
import { initialState } from './initialState';

function exerciseReducer(state = initialState, action) {
  switch (action.type) {
    case actionTypes.ADD_EXERCISE:
      const { exercise } = action.payload;
      const workoutId = state.activeWorkout;

      console.log('initialState.activeWorkout:', initialState.activeWorkout);
      console.log('state:', state);
      console.log('workoutId:', workoutId);

      // if (!workoutId) {
      //   console.error(
      //     'No active workout or workout does not exist:',
      //     workoutId
      //   );
      //   return state;
      // }

      return {
        ...state,
        workouts: {
          ...state.workouts,
          [workoutId]: {
            ...workoutId, // Spread the existing workout object
            exercises: [...workoutId.exercises, exercise]
          }
        }
      };

    case actionTypes.DELETE_EXERCISE:
      const { workoutId: wId, exerciseId } = action.payload;

      if (!wId || !state.workouts[wId]) {
        console.error('No active workout or workout does not exist:', wId);
        return state;
      }

      // Filter out the exercise to be deleted
      const filteredExercises = state.workouts[wId].exercises.filter(
        e => e.id !== exerciseId
      );
      return {
        ...state,
        workouts: {
          ...state.workouts,
          [wId]: {
            ...state.workouts[wId],
            exercises: filteredExercises
          }
        }
      };

    default:
      return state;
  }
}

export { exerciseReducer };
