import React, { useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { ProgramContext } from '../../../contexts/programContext';
import Workout from '../../../components/Workout/Workout';
import ProgramForm from '../../../components/Program/ProgramForm';
import NavBar from '../../../components/Nav/Nav';
import Toggle from '../../../components/Inputs/Toggle';
import Button from '../../../components/Inputs/Button';
import { v4 as uuidv4 } from 'uuid';
import './program.css';

const CreateProgram = () => {
  const { state, dispatch, addWorkout, setActiveWorkout, clearState } =
    useContext(ProgramContext);
  const [expandedWorkouts, setExpandedWorkouts] = useState({});

  const selectedProgram = state.program;
  const selectedWorkoutId = state.selectedWorkoutId;

  // console.log('selectedProgram:', selectedProgram);
  // console.log('selectedWorkoutId:', selectedWorkoutId);

  const navigate = useNavigate();

  useEffect(() => {
    // console.log(
    //   'CreateProgram useEffect - state:',
    //   JSON.stringify(state, null, 2)
    // );
    if (!state.program) {
      console.error('No program in state');
    } else {
      // console.log('Selected Program:', JSON.stringify(state.program, null, 2));
    }
  }, [state]);

  const saveProgramToServer = async program => {
    const programToSave = {
      ...program,
      workouts: Array.isArray(program.workouts)
        ? program.workouts.map(workout => ({
            ...workout,
            exercises: workout.exercises.map(exercise => {
              const { tempId, ...exerciseToSave } = exercise;
              return exerciseToSave;
            })
          }))
        : [] // Fallback to empty array if workouts is not defined
    };

    try {
      const response = await fetch(
        program.id
          ? `http://localhost:9025/api/programs/${program.id}`
          : 'http://localhost:9025/api/programs',
        {
          method: program.id ? 'PUT' : 'POST', // Use PUT for updating, POST for creating
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(programToSave)
        }
      );

      if (!response.ok) {
        const errorText = await response.text();
        console.error('Error saving program:', errorText);
        throw new Error('Failed to save program');
      }

      const savedProgram = await response.json();
      return savedProgram;
    } catch (error) {
      console.error('Failed to save program:', error);
      throw error;
    }
  };

  const handleSaveProgram = async () => {
    try {
      const currentProgram = state.program;

      if (!currentProgram || !Array.isArray(currentProgram.workouts)) {
        throw new Error(
          'Program is missing workouts or workouts is not an array'
        );
      }

      const savedProgram = await saveProgramToServer(currentProgram);

      // Update state with new IDs from the server
      dispatch({
        type: 'UPDATE_PROGRAM',
        payload: {
          ...savedProgram,
          workouts: savedProgram.workouts.map(w => ({
            ...w,
            exercises: w.exercises.map(ex => ({
              ...ex,
              id: ex.id || uuidv4() // Fallback if the server didn't return an ID
            }))
          }))
        }
      });

      navigate('/programs');
    } catch (error) {
      console.error('Failed to save the program:', error);
    }
  };

  const handleExpandWorkout = workoutId => {
    // console.log('handleExpandWorkout called with workoutId:', workoutId);
    // console.log('Current expandedWorkouts:', expandedWorkouts);

    setExpandedWorkouts(prevState => {
      const newState = {
        ...Object.keys(prevState).reduce((acc, key) => {
          acc[key] = false;
          return acc;
        }, {}),
        [workoutId]: !prevState[workoutId]
      };
      // console.log('New expandedWorkouts state:', newState);
      return newState;
    });

    setActiveWorkout(workoutId);
  };

  const handleToggleProgramForm = () => {
    setExpandedWorkouts(prevState => ({
      ...Object.keys(prevState).reduce((acc, key) => {
        acc[key] = false; // collapse all workouts
        return acc;
      }, {}),
      program: !prevState.program // toggle the program form
    }));
  };

  const handleCancel = () => {
    clearState();
    navigate('/');
  };

  const handleAddWorkout = event => {
    event.preventDefault();
    const currentProgramId = Object.keys(state.programs)[0];
    if (!currentProgramId) {
      console.error('No current program ID found');
      return;
    }
    addWorkout(currentProgramId);
  };

  if (!selectedProgram) {
    return <div>Loading or no programs available...</div>;
  }

  return (
    <div>
      <NavBar isEditing='true' />
      <div className='create-prog-page'>
        <div className='create-prog-page__toggle-container'>
          <Toggle />
        </div>
        <div className='create-prog-page__container'>
          <div className='create-prog-page__left-container'>
            <ProgramForm
              program={selectedProgram}
              isEditing={true}
              isNewProgram={true}
              isExpanded={expandedWorkouts['program']}
              onToggleExpand={handleToggleProgramForm}
            />
            {selectedProgram.workouts && selectedProgram.workouts.length > 0 ? (
              selectedProgram.workouts.map(workout => {
                if (!workout || !workout.id) {
                  console.error('Invalid workout object:', workout); // Log invalid workout object
                  return null;
                }
                return (
                  <Workout
                    key={workout.id}
                    workout={workout}
                    isNewProgram={true}
                    isExpanded={expandedWorkouts[workout.id] || false}
                    onToggleExpand={() => handleExpandWorkout(workout.id)}
                  />
                );
              })
            ) : (
              <div>No workouts available</div>
            )}
          </div>
        </div>
        <div className='create-prog-page__button-container'>
          <Button type='button' onClick={handleAddWorkout}>
            Add Workout
          </Button>
          <Button type='submit' onClick={handleSaveProgram}>
            Save
          </Button>
          <Button type='button' onClick={handleCancel}>
            Cancel
          </Button>
        </div>
      </div>
    </div>
  );
};

export default CreateProgram;
