const Button = ({
  id,
  type = 'button',
  onClick,
  children,
  bgColor,
  fontSize
}) => (
  <div
    class='box-1'
    id={id}
    type={type}
    onClick={onClick}
    bgColor={bgColor}
    fontSize={fontSize}
  >
    <div class='btn'>
      <span>HOVER ME</span>
    </div>
    {children}
  </div>
);

export default Button;
