import React from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { CardActionArea } from '@mui/material';

export default function Exercise({
  name,
  muscle,
  equipment,
  image,
  onClick,
  isSelected
}) {
  const className = isSelected ? 'selected' : '';

  const cardStyle = {
    maxWidth: 345
    // Remove static height to allow card to expand based on content
    // You can set minHeight if necessary
  };

  const cardMediaStyle = {
    // Adjust these styles based on your needs
    objectFit: 'cover' // 'contain' if you want to see the whole image
    // Remove static height, let the image define height
  };

  return (
    <Card sx={{ maxWidth: 345 }} onClick={onClick} className={className}>
      <CardActionArea>
        <CardMedia
          component='img'
          src={image}
          alt={name}
          sx={{
            width: 'auto',
            height: 500,
            maxHeight: 850,
            objectFit: 'contain'
          }}
        />
        <CardContent>
          <Typography gutterBottom variant='h5' component='div'>
            {name} ({equipment})
          </Typography>
          <Typography variant='body1' color='text.secondary'>
            {muscle}
          </Typography>
        </CardContent>
      </CardActionArea>
    </Card>
  );
}
