import React, { useState, useEffect } from 'react';
import './homePage.css';

const HomePage = () => {
  const [showScrollDown, setShowScrollDown] = useState(true);
  const [showScrollUp, setShowScrollUp] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      const viewportHeight = window.innerHeight;
      console.log('scrollPosition:', scrollPosition);
      console.log('viewportHeight:', viewportHeight);

      // Show scroll down arrow when at the top
      setShowScrollDown(scrollPosition < viewportHeight);

      // Show scroll up arrow when not at the top
      setShowScrollUp(scrollPosition >= viewportHeight);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToNextDiv = direction => {
    const divHeight = window.innerHeight;
    window.scrollTo({
      top: direction === 'down' ? divHeight : 0,
      behavior: 'smooth'
    });
  };

  return (
    <div className='App'>
      <div className='div-container' style={{ height: '100vh' }}>
        <div className='div-1'>Div 1</div>
        {showScrollDown && (
          <div className='scroll-down' onClick={() => scrollToNextDiv('down')}>
            &#8595;
          </div>
        )}
      </div>
      <div className='div-container' style={{ height: '100vh' }}>
        {showScrollUp && (
          <div className='scroll-up' onClick={() => scrollToNextDiv('up')}>
            &#8593;
          </div>
        )}
        <div className='div-2'>Div 2</div>
      </div>
    </div>
  );
};

export default HomePage;
