import { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useInView } from 'react-intersection-observer';
import NavBar from '../../components/Nav/Nav';
import './home.css';

const HomePage = () => {
  const [activeDiv, setActiveDiv] = useState(null);
  const navigate = useNavigate();

  const { ref: ref1, inView: inView1 } = useObservedRef(0.4);

  const childRef1 = useObservedRef(0.4);
  const childRef2 = useObservedRef(0.4);
  const childRef3 = useObservedRef(0.4);

  const childRefs = useRef([childRef1, childRef2, childRef3]);

  const navigateToProgramPage = () => {
    navigate('/programs');
  };

  const navigateToCreateProgramPage = () => {
    navigate('/create-program');
  };

  const scrollToNextPage = () => {
    window.scrollTo({
      top: window.innerHeight,
      behavior: 'smooth'
    });
  };

  const handleClick = divId => {
    setActiveDiv(activeDiv === divId ? null : divId);
  };

  const handleMouseEnter = divId => {
    setActiveDiv(divId);
  };

  const handleMouseLeave = () => {
    setActiveDiv(null); // Optional: Remove active state on mouse leave
  };

  return (
    <div>
      <NavBar />
      <div className='full-viewport-div'>
        <h1 className='main-title'>Pr00f 0f W0rk</h1>
        <div
          className={inView1 ? 'arrow-hidden' : 'arrow'}
          onClick={scrollToNextPage}
        >
          â†“
        </div>
      </div>
      <div className='container'>
        <div
          className={`child left ${activeDiv === 'div1' ? 'active' : ''}`}
          onClick={navigateToCreateProgramPage}
          onMouseEnter={() => handleMouseEnter('div1')}
          onMouseLeave={handleMouseLeave}
          ref={childRefs.current[0]?.ref}
        >
          <h2>Programs</h2>
          <h1>Start Program</h1>
        </div>
        <div
          className={`child center ${activeDiv === 'div2' ? 'active' : ''}`}
          onClick={navigateToProgramPage}
          onMouseEnter={() => handleMouseEnter('div2')}
          onMouseLeave={handleMouseLeave}
          ref={childRefs.current[1]?.node}
          style={{ opacity: childRefs.current[1]?.inView ? 1 : 0.2 }}
        >
          <h2>Workout</h2>
          <h1>Workout Using Program</h1>
        </div>
        <div
          className={`child right ${activeDiv === 'div3' ? 'active' : ''}`}
          onClick={() => handleClick('div3')}
          onMouseEnter={() => handleMouseEnter('div3')}
          onMouseLeave={handleMouseLeave}
          ref={childRefs.current[2]?.node}
          style={{ opacity: childRefs.current[2]?.inView ? 1 : 0.2 }}
        >
          <h2>Workout</h2>
          <h1>Quick Workout</h1>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
