import { actionTypes } from '../actions/actionTypes';

function exerciseReducer(state = {}, action) {
  switch (action.type) {
    case actionTypes.SET_ACTIVE_WORKOUT:
      return {
        ...state,
        activeWorkout: action.payload
      };

    case actionTypes.ADD_EXERCISE:
      console.log('ADD_EXERCISE action:', action);
      const { workoutId, exercises } = action.payload;

      if (state.activeWorkout && state.activeWorkout.id === workoutId) {
        return {
          ...state,
          activeWorkout: {
            ...state.activeWorkout,
            exercises: [...state.activeWorkout.exercises, ...exercises]
          }
        };
      } else {
        console.error('activeWorkout is undefined or workoutId does not match');
      }
      return state;

    case actionTypes.UPDATE_EXERCISE:
      console.log('UPDATE_EXERCISE action:', action);
      if (state[action.payload.id]) {
        return {
          ...state,
          [action.payload.id]: {
            ...state[action.payload.id],
            ...action.payload.updates
          }
        };
      } else {
        console.error(
          'Exercise id does not exist in state:',
          action.payload.id
        );
      }
      return state;

    case actionTypes.DELETE_EXERCISE:
      console.log('DELETE_EXERCISE action:', action);
      const newState = { ...state };
      delete newState[action.payload];
      return newState;

    default:
      return state;
  }
}

export { exerciseReducer };
